<script type="text/javascript">
	function updateMark(event, object, student, type)
	{
		var key = window.event ? event.keyCode : event.which;
		
		if(key==13){
			if(object.value != "")
			{
				$.ajax({
					type: "GET",
					url: "/admin/mark/set/<?php echo $group['id']; ?>/<?php echo $course['id']; ?>/" + student + "/" + object.value + "/" + type + "/<?php echo $session['token']; ?>",
					data: {
					}
				}).done(function(data) {

				});
			}
		}
	}
</script>

