<?php
	function getMarkType($type)
	{
		switch ($type) {
			case "1":
				echo "CE";
			break;

			case "2":
				echo "DE";
			break;

			case "3":
				echo "TP";
			break;

			case "4":
				echo "Projet";
			break;
		}
	}

	function moyenne($course, $marks)
	{
		$moyenne = 0;

		foreach ($marks as $key => $value) {
			if($value['course']['id'] == $course['id'])
			{
				$moyenne += $value['value'] * $value['coeff'];
			}
		}

		return $moyenne;
	}
